			return {
				title: null,
				bigimg: null,
				getid: 0,
				xiangxi: null,
				mylist: {},
				outtime: null,
				singer: false,
				keyong: false,
				getshou: [],
				topPopup: false,
				collect: "请先登录",
				myhash: null,
				yandn: false,
				mtss: null,
				isShow: false,
				gaoll:null
			}
		},

		methods: {

			sort: function() {

				var salf = this
				axios({
						url: "https://bird.ioliu.cn/v1?url=http://m.kugou.com/singer/info/" + salf.getid + "&json=true",
						method: 'get'
					})
					.then((response) => {

						salf.title = response.data.info.singername;
						var res = response.data.info.imgurl.replace('{size}', '400');

						salf.xiangxi = response.data.info.profile;
						salf.outtime = response.data.info.intro;

						salf.bigimg = res;
						var arry = [];
						var name = [];

						response.data.songs.list.forEach(function(item) {
							var obj = {};
							obj.name = item.filename.split(' - ')[1];
							obj.list = item.filename.split(' - ')[0];
							obj.hash = item.hash

							arry.push(obj);

						});

						salf.mylist = arry;

					})
					.catch((error) => {
						console.log(error);
					});
			},
			open(position) {
				this[position + 'Popup'] = true
			},
			close(position) {
				this[position + 'Popup'] = false
			},
			goback: function() {
				history.back()
			},
			changeDirection: function() {

				this.$store.state.isshow = true;
			},
			setImg(songsPlay,mindex) {
				var arr = [songsPlay];
				this.gaoll = mindex;
				this.$store.dispatch('setImg', [this.bigimg, true]);
				this.$store.dispatch('setSongs', arr);
			},

			jiantin: function(hash) {

				this.$store.state.getshou = this.getshou;
				this.myhash = hash;
			}
		},
		mounted() {

			this.getshou = this.$store.state.getshou;

			this.$store.state.showPlay = true;

			this.getid = this.$route.params.id;

			this.sort();

			var salf = this;

			function getCookie(name) {
				var a;
				var reg = new RegExp("(^|)" + name + "=([^;]*)(;|$)");
				if(a = document.cookie.match(reg)) {
					return a[2];
				}

			};
		
			if(getCookie("tel") === undefined) {
			
				this.isShow = true;

			} else {
				
			}
			

			$(".liebiao").on("click", "li", function() {

				var myimg = $("<img/>").attr({
					src: "data:image/png;base64,"
				}).css({
					width: "20",
					position: "absolute",
					'left': '5px',
					'top': '15px'
				});

				var gaodu = $(".play").offset().top - $(this).offset().top;

				$(this).append(myimg);

				myimg.animate({
					top: 0
				}, function() {
					myimg.animate({
						top: gaodu
					}, 1000, function() {
						myimg.remove()
					})

				})
			})

		},
		watch: {
			topPopup(val) {
				if(val) {
					setTimeout(() => {
						this.topPopup = false
					}, 1000)
				}
			},

		},
		updated() {

			var salf = this;
			$('.demo-checkbox').on("click", function() {
				if(salf.isShow){
					salf.topPopup = true;
				}else{
						salf.topPopup = false;
				}
			
			})
		},
		computed: {
			news() {
				//				console.log(this.myhash);
				if(this.getshou == "") {

				} else {
					this.$store.state.getshou = this.getshou;
				}

				return this.getshou
			}
		},
		//拿到布尔值
		components: {
			xstom,
			xtop,
			xplay
		}
	}
</script>

